INSERT INTO es_goods_<userid>_<siteid> (goods_id,name,sn,brand_id,cat_id,type_id,goods_type,unit,weight,market_enable,image_default,image_file,brief,intro,price,cost,mktprice,params,specs,have_spec,adjuncts,create_time,last_modify,view_count,buy_count,disabled,store,point,page_title,meta_keywords,meta_description,p20,p19,p18,p17,p16,p15,p14,p13,p12,p11,p10,p9,p8,p7,p6,p5,p4,p3,p2,p1,sord) VALUES (44,'ThinkPad E40（0578-5KC）14.0英寸笔记本','219117',2,21,11,'normal',null,0.000,1,'http://static.enationsoft.com/attachment/it360/goods/201007151735013249.jpg','http://static.enationsoft.com/attachment/it360/goods/201007151735013249.jpg,http://static.enationsoft.com/attachment/it360/goods/201007151735072287.jpg,http://static.enationsoft.com/attachment/it360/goods/201007151735121655.jpg,http://static.enationsoft.com/attachment/it360/goods/201007151735160945.jpg,http://static.enationsoft.com/attachment/it360/goods/201007151735207809.jpg',null,'<table border="0" cellpadding="0" cellspacing="0" height="100" width="750">	<tbody>		<tr>			<td>				<p>					<strong>别具创意的全新设计</strong><br />					<span><strong>ThinkPad E40（0578-5KC）</strong></span> 思迥异，做不同。锐意进取，优美流畅，摒弃浮华，洗却铅华，仅以清晰线条和大胆色彩彰显ThinkPad卓越性能。</p>			</td>		</tr>		<tr>			<td>				<p align="center">					<img border="0" height="283" src="http://img20.360buyimg.com/ImgUpload/vclistimg/20104211157235602010-04-21_113438.png" width="471" /></p>			</td>		</tr>		<tr>			<td>				<p>					联想ThinkPad Edge E40搭载英特尔新移动平台，内置了酷睿i3处理器。跟E30相比，其性能大幅度提升。它完全可以用于文件操作、看电影、听音乐，轻松支持720p和 1080p解码。</p>				<p>					<br />					联想ThinkPad E40的续航时间和散热能力</p>				<p>					<br />					&nbsp;&nbsp;&nbsp; 测试者将屏幕亮度调整到70%、无线活跃、系统为均衡状态，联想ThinkPad E40的续航时间达到了4小时33分钟。其表现比较出色。</p>				<p>					<br />					&nbsp;&nbsp;&nbsp; 联想ThinkPad E40的双散热孔设计提升了其散热效果。</p>				<p>					<br />					外观 与E30一脉相承 ThinkPad中的时尚派代表</p>				<p>					<br />					E40继续采用工程塑料外壳，顶盖的强度维持了以往的水准，使用手指按压顶盖并没有出现明显的下陷情况。</p>				<p>					<br />					E40 A面表面采用了流行的类钢琴漆效果处理，这样的效果虽然看起来非常时尚，但是避免不了指纹的沾染是这种效果的最大软肋，E40机身的边角处均采用了圆滑过 渡处理。A面设计的比较简单，只有一个ThinkPad LOGO，同时ThinkPad LOGO上i字母的点仍然采用了此前在SL系列上出现过的&ldquo;红点&rdquo;设计，在开机状态下镶嵌于LOGO上的发光二极管会发出红光，直接告诉观看者这款产品 &ldquo;红点&rdquo;的身份。<br />					B面与C面仍然是黑色为主，磨砂效果边框与镜面屏幕的组合不失时尚，同时也较有效的避免了当前大部分产品钢琴漆边框与镜面屏组合 造成的视觉问题，摄像头仍然被设计在了屏幕上边框的中央，而麦克风则被设计在了摄像头的右侧位置，而此前的E30则是设计在了左手掌托附近的位置，这样的 设计可以实现更好的拾音效果。而E40的音箱则被设计在了下屏幕边框中央位置。</p>				<p>					<br />					E40的C面风格与此前我们评测过的E30键盘区域类似，大体维持了ThinkPad用户都比较熟悉的风格，键盘也是采用了流行的分离式设 计，也就是俗称的&ldquo;巧克力&rdquo;键盘，但是与很多超立体键盘不同的是，这款产品的按键表面并不是一个平面，而是在中间有一个向下的凹陷，弧度正好可以容纳普通 人手指的前端。</p>				<p>					<br />					E40也是配备了TrackPoint+TouchPad双重装置，这个被称为UltraNav的组合以往更多的出现在了T系列产品上，而 由IBM公司发明了叫做&ldquo;TrackPoint&rdquo;（小红帽）的指点杆无疑已经成为了ThinkPad产品的代名词之一，而TrackPoint的出色手感 也是很多用户仍然坚守在ThinkPad产品上的理由之一，尽管市场上有一些其他品牌也配备指点杆的产品，但是TrackPoint无疑是其中最好用的。 TouchPad触摸板主体采用了一体化设计，表面采用亚光磨砂效果处理，触摸板的面积较大，支持多点触摸。</p>			</td>		</tr>	</tbody></table>',5399.000,5000.000,5630.000,'[{"name":"主体","paramList":[{"name":"系列","value":"联想 ThinkPad ","valueList":[]},{"name":"型号","value":"E40 05785KC","valueList":[]},{"name":"颜色","value":"亮光黑","valueList":[]},{"name":"平台","value":"Intel","valueList":[]}],"paramNum":4},{"name":"软件","paramList":[{"name":"操作系统","value":"Microsoft Windows 7 Home Basic 家庭普通版","valueList":[]}],"paramNum":1},{"name":"处理器","paramList":[{"name":"CPU类型","value":"Intel Core i3-330M","valueList":[]},{"name":"速度","value":"2.13GHz","valueList":[]},{"name":"系统总线","value":"2.5 GT/s","valueList":[]},{"name":"二级缓存","value":"3MB","valueList":[]}],"paramNum":4},{"name":"芯片组","paramList":[{"name":"芯片组","value":"Intel HM55","valueList":[]}],"paramNum":1},{"name":"内存","paramList":[{"name":"内存容量","value":"2GB","valueList":[]},{"name":"内存类型","value":"DDR3 1066","valueList":[]},{"name":"插槽数量","value":"2 x SO-DIMM","valueList":[]},{"name":"最大支持容量","value":"4GB","valueList":[]}],"paramNum":4},{"name":"硬盘","paramList":[{"name":"硬盘容量","value":"320GB","valueList":[]},{"name":"转速","value":"5400转/分钟","valueList":[]},{"name":"接口类型","value":"SATA 串行","valueList":[]}],"paramNum":3},{"name"