INSERT INTO es_article_<userid>_<siteid> (id,title,content,create_time,cat_id) VALUES (28,'招聘信息','<div class="pageWrap"> <h2>招聘信息</h2> <h4>我的工作！</h4> <ul class="list"> <li>中国电子商务发展的领跑者；</li> <li>面向全世界的网上消费者提供几十个类别共计百万种商品；</li> <li>方便超值的网络购物，每天让近千万的网上消费者轻松得到精神和物质上的收获，让你的生活更多愉悦；</li> </ul> <h4>快来加入！</h4> 或许我们曾经陌路，或许我们曾经是对手，但我们可能是更好的工作伙伴。<br> 这里将给予你飞翔的天空、畅游的海洋、驰骋的疆土。<br> 这里是一个任由你发挥创造的平台！<br> 这里所需要的仅仅是你的热忱与才智！<br> 诚邀加盟，一起收获！共同愉悦！<br> </div>',1270540058647,9999);
